import { useEffect, useRef } from 'react';
import { NextPage } from 'next';
import fetch from 'isomorphic-unfetch';
import _ from 'lodash';
import { get } from 'lodash/fp';

import { Card, Input } from 'antd';
import LandingLayout from '../../containers/LandingLayout';

const { Meta } = Card;

interface MovieItem {
  id: string;
  name: string;
  summary: string;
  image: any;
}

interface Props {
  movies: Array<MovieItem>;
}

const MoviesPage: NextPage<Props> = ({ movies }) => {
  const searchInput = useRef(null);

  useEffect(() => {
    console.log(movies);
  }, [movies]);

  const renderMovies = (): Array<JSX.Element> => {
    return movies.map(movie => {
      const movieSummary = _.replace(movie.summary, /<[/]?[pb]>/g, '');
      return (
        <Card
          key={movie.id}
          hoverable
          style={{ width: 240 }}
          cover={<img alt="example" src={movie.image.medium} />}
        >
          <Meta title={movie.name} description={movieSummary} />
        </Card>
      )
    })
  }

  return (
    <LandingLayout>
      <p>Movies page</p>
      <Input value="sherlock" ref={searchInput} placeholder="Title movie name to search" style={{marginBottom: '24px'}} />
      <div className="movies-list">
        {renderMovies()}
      </div>
    </LandingLayout>
  )
}

MoviesPage.getInitialProps = async () => {
  const res = await fetch('https://api.tvmaze.com/search/shows?q=sherlock');
  const data = await res.json();

  return {
    movies: data.map(entry => get('show', entry)),
  }
}

export default MoviesPage;
