import React, { FC } from 'react';
import _ from 'lodash';

import { Icon, Layout, Menu } from 'antd';

import { MenuItem } from './types';
import LeftMenu from './Menu.constant';

const { Sider } = Layout;
const { SubMenu } = Menu;

const LandingLeftSider: FC = () => {
  const renderSubMenu = ( menuItem: MenuItem ): JSX.Element => (
    <SubMenu
      key={menuItem.id}
      title={
        <span>
          <Icon type={menuItem.antIcon} />
          <span>{menuItem.label}</span>
        </span>
      }
    >
    {
      menuItem.children.map(subMenuItem => (
        <Menu.Item key={subMenuItem.id}>{_.capitalize(subMenuItem.label)}</Menu.Item>
      ))
    }
    </SubMenu>
  )

  const renderLeftMenu = (): Array<JSX.Element> => {
    return (
      LeftMenu.map(menu => (
        <Menu
          key={menu.id}
          mode="inline"
          defaultSelectedKeys={['1']}
          defaultOpenKeys={['sub1']}
          style={{ height: '100%', borderRight: 0 }}
          >
            { menu.children ? renderSubMenu(menu) : <Menu.Item key={menu.id} title={<span><Icon type={menu.antIcon} /><span>{menu.label}</span></span>}></Menu.Item> }
        </Menu>
      ))
    )
  }

