import React, { FC } from 'react';
import _ from 'lodash';

import { Breadcrumb, Icon, Layout, Menu, } from 'antd';
import LandingHeader from './LandingHeader';

const { Content, Sider, } = Layout;
const { SubMenu } = Menu;

interface Props {
  path?: string;
  title?: string;
}

const LandingLayout: FC<Props> = ({ children, path, title }) => {
  const renderBreadcumbs = (): Array<JSX.Element> => {
    const breadcumbElements: Array<string> = _.split(path, '/') || ['Home'];
    return breadcumbElements.map(breadcumb => (
      <Breadcrumb.Item key={breadcumb} >{_.capitalize(breadcumb)}</Breadcrumb.Item>
    ))
  }

  return (
    <Layout>
      <LandingHeader title />
      <Layout>
        <Sider width={200} style={{ background: '#fff' }}>
          <Menu
            mode="inline"
            defaultSelectedKeys={['1']}
            defaultOpenKeys={['sub1']}
            style={{ height: '100%', borderRight: 0 }}
          >
            <SubMenu
              key="sub1"
              title={
                <span>
                  <Icon type="user" />
                  subnav 1
                </span>
              }
            >
              <Menu.Item key="1">option1</Menu.Item>
              <Menu.Item key="2">option2</Menu.Item>
            </SubMenu>
            <SubMenu
              key="sub2"
              title={
                <span>
                  <Icon type="laptop" />
                  subnav 2
                </span>
              }
            >
              <Menu.Item key="3">option3</Menu.Item>
              <Menu.Item key="4">option4</Menu.Item>
              <Menu.Item key="5">option5</Menu.Item>
            </SubMenu>
            <SubMenu
              key="sub3"
              title={
                <span>
                  <Icon type="notification" />
                  subnav 3
                </span>
              }
            >
              <Menu.Item key="7">option7</Menu.Item>
              <Menu.Item key="8">option8</Menu.Item>
            </SubMenu>
          </Menu>
        </Sider>
        <Layout style={{ padding: '0 24px 24px' }}>
          <Breadcrumb style={{ margin: '16px 0' }}>
            {renderBreadcumbs()}
          </Breadcrumb>
          <Content
            style={{
              background: '#fff',
              padding: 24,
              margin: 0,
              minHeight: 280,
            }}
          >
            {children}
          </Content>
        </Layout>
      </Layout>
    </Layout>
  )
}

export default LandingLayout;
