import { useState, useRef } from 'react';
import { NextPage } from 'next';
import fetch from 'isomorphic-unfetch';
import { get } from 'lodash/fp';

import { Card, Input } from 'antd';
import LandingLayout from '../../containers/LandingLayout';

const { Meta } = Card;

interface MovieItem {
  id: string;
  title: string;
  description: string;
  image: any;
}

interface Props {
  movies: Array<MovieItem>;
}

const MoviesPage: NextPage<Props> = ({ movies }) => {
  const searchInput = useRef(null);

  const renderMovies = (): Array<JSX.Element> => {
    return movies.map(movie => (
      <Card
        key={movie.id}
        hoverable
        style={{ width: 240 }}
        cover={<img alt="example" src={movie.image} />}
      >
        <Meta title={movie.title} description={movie.description} />
      </Card>
    ))
  }

  return (
    <LandingLayout>
      <p>Movies page</p>
      <Input value="sherlock" ref={searchInput} placeholder="Title movie name to search" />
      <div className="movies-list">
        {renderMovies()}
      </div>
    </LandingLayout>
  )
}

MoviesPage.getInitialProps = async () => {
  const res = await fetch('https://api.tvmaze.com/search/shows?q=sherlock');
  const data = await res.json();

  return {
    movies: data.map(entry => get('show', entry)),
  }
}

export default MoviesPage;
